import React, {Component} from 'react';

class Stations extends Component {
  render(){
    return(
      <div>
        <h2>Nome: {this.props.nome}</h2>
        <h2>Cargo: {this.props.cargo}</h2>
      </div>
    )
  }
}

class App extends Component {

  constructor(props){
    super();
    this.state = {
      number: 0
    }
    this.somar = this.somar.bind(this);
    this.subtrair = this.subtrair.bind(this);
  }

  somar(){
    let num = this.state.number;
    num = num + 1;
    this.setState({number: num});
  }

  subtrair(){
    let numSub = this.state.number;
    numSub = numSub - 1;
    this.setState({number: numSub});
  }

  render(){
    return(
      <div class='container'>
        <h1>Contador</h1>
          <button onCLick={this.subtrair}>-</button>
          <h2>{this.state.number}</h2>
          <button onClick={this.somar}>+</button>
      </div>
    )
  }
}
export default App;
