import React, {useEffect, useState} from 'react';
import { Chart } from "react-google-charts";
import {Bar} from 'react-chartjs-2'; 

import './home.css';
import { axiosService } from '../../service/axios';

function Home() {

    const [options, setOptions] = useState({
        title: 'Gráfico de Pizza'
    })
    const [data, setData] = useState([])
    const [products, setProducts] = useState({
        labels: ['January', 'February', 'March',
    'April', 'May'],
        datasets:{
            label: 'Rainfall',
            backgroundColor: 'rgba(75,192,192,1)',
            borderColor: 'rgba(0,0,0,1)',
            borderWidth: 2,
            data: [65, 59, 80, 81, 56]
          }
    })
    const [labels, setLabels] = useState(['January', 'February', 'March',
    'April', 'May'])
    const [datasets, setDatasets] = useState({
        label: 'Rainfall',
        backgroundColor: 'rgba(75,192,192,1)',
        borderColor: 'rgba(0,0,0,1)',
        borderWidth: 2,
        data: [65, 59, 80, 81, 56]
      })
    // const [data, setData] = useState([
    //     ['Linguagens', 'Quantidade'],
    //     ['React', 100],
    //     ['Angula', 80],
    //     ['Vue', 50],
    // ])
    
    useEffect(() => {
        loadDashProductValue();
        setProducts({labels: labels, datasets:datasets})
    },[]);

    async function loadDashProductValue() {
        axiosService({
            method:'GET',
            url:'/recycling/mktProductValue',
            params:{
                page:0,
                size:5
            }
        }).then(response => {
            let values = [];
            let products = [['Produtos', 'Valor']];
            let resp = [];
            resp = response.data.listRecyclingProductsValue;
            
            console.log(resp)
            resp.map(p => {
                products.push([
                    p.product.product,
                    parseFloat(p.averageValue.replace(',', '.'))
                ]);          
            })
            setData(products);
            console.log('produtos: ' + products);
        }).catch(error => {

        })
    };




        
    return(
        <div id='containerPage'>
            <div id='containerResultFind'>
                
            <Bar
                data={products}
                options={{
                    title:{
                    display:true,
                    text:'Average Rainfall per month',
                    fontSize:20
                    },
                    legend:{
                    display:true,
                    position:'right'
                    }
                }}
                />
                
            </div>
        </div>
    )
    
}
export default Home;

