import React, {useState, useEffect} from 'react';

import './product.css';
import { axiosService } from '../../service/axios';
// import Paginator from '../../components/Paginator';

function Product()  {
    
    const [products, setProducts] = useState([]);
    const [mktProducts, setMktProducts] = useState([]);
    const [pages, setPages] = useState([]);
    const [pagesSegment, setPagesSegment] = useState([]);
    const [sort, setSort] = useState();
    const [page, setPage] = useState(0);
    const [size] = useState(1);
    const [idProduct, setIdProduct] = useState();
    const [totalPages, setTotalPages] = useState();
    // const [totalElements, setTotalElements] = useState();
        

    useEffect(() => {
       loadProducts();
       loadMktProducts();
       let pages = [];
       for(let i = 1; i < totalPages + 1; i++) {
           pages.push(i);
       }
       setPages(pages)
    },[totalPages])

    async function loadProducts() {
        try {
            const response = await axiosService({
                url: '/product/find',
                method:'GET',
                params: {
                    idProduct: '',
                    page: 0,
                    size: '0',
                    sort: '',
                    sortDirection: ''
                }
            })
            console.log(response);
            setProducts(response.data.response);
        } catch (error) {
            console.log(error.message);
        }
    }

    async function loadMktProducts(){
        try {
            const response = await axiosService({
                url: '/marketProduct/find', method:'GET',
                params: {
                    idProduct: idProduct,
                    page: page,
                    size: size,
                    sort: sort,
                    sortDirection: ''
                }
            })
            console.log(response)
            setMktProducts(response.data.response);
            // setTotalElements(response.data.totalElements);
            setTotalPages(response.data.totalPages);
        } catch (error) {
            console.log(error.message);
        }
        loadPages();
    }

    function sortFind(e) {
        setSort(e.target.value)
    }

    function productFind(e) {
        setIdProduct(e.target.value)
    }

    function find() {
        console.log(idProduct);
        console.log(sort);
    }

    function loadPages() {
        debugger;
        let pagesThis = [];
        let segment = [];
        let pg = page + 1;
        console.log(totalPages)
        for(let i = 1; i < totalPages + 1; i++) {
            pages.push(i);
        }

        segment = pagesThis.slice(pg-2 >= 0 ? pg-2 : 0, pg+2 <= pagesThis.length ? pg+2 : pagesThis.length);
        
        setPages(pagesThis);
        setPagesSegment(segment);
        console.log(segment)

    }

    function changePage(page){
        debugger;
        let segment = [];
        let pagesThis = pages;
        let quantExibPages = 3;
        let init = (page - (quantExibPages-1) >= 0) ? page - (quantExibPages-1) : 0;
        let limit = (init + quantExibPages < pagesThis.length) ? init + quantExibPages : pagesThis.length;
        console.log('pages.length: ' + pagesThis.length)
        console.log(page)
        page = page - 1;
        page = (page > totalPages) ? totalPages-1 : page;
        page = (page <= 0) ? 0 : page;
        segment = pagesThis.slice(init, limit);
        if(limit === pagesThis.length && (limit - quantExibPages) >= 0) {
            segment = pagesThis.slice(limit-quantExibPages, limit);
        }
        setPage(page);     
        setPagesSegment(segment)
    }

    function leftPage(page) {
        console.log(page);
        if(page - 1 >= 0) {
            let nextPage = page;
            changePage(nextPage)
        }
    }

    function rightPage(page) {
        console.log(page);
        debugger;
        if(page + 2 > 0 || page + 2 < pages.length) {
            let nextPage = page + 2;
            changePage(nextPage)
        }
    }

 
       
        return(
            <div id='containerProducts'>
                <div id='titleProducts'>Produtos por posto de coleta</div>
                <div id='containerFind'>
                    <div id='containerSelect'>
                        <label id='labelSelectFind'>Produto: </label>
                        <select id='selectFind' name='idProduct' value={idProduct} onChange={productFind}>
                                    <option id='selectFindOpt' key={0} value=''>Selecione...</option>
                            {products.map((p) => {
                                return(
                                    <option id='selectFindOpt' key={p.id} value={p.id}>{p.product}</option>
                                )
                            })}
                        </select>
                    </div>
                    <div id='containerSelect'>
                        <label id='labelSelectFind'>Ordem: </label>
                        <select id='selectFind' name='sort' value={sort} onChange={sortFind}>
                            <option id='selectFindOpt' value=''>Selecione...</option>
                            <option id='selectFindOpt' value='Valor'>Valor</option> 
                            <option id='selectFindOpt' value='Produto'>Produto</option>
                            <option id='selectFindOpt' value='Posto'>Posto de coleta</option>
                        </select>
                    </div>
                    <button id='btnFindProduct' onClick={find}>Buscar</button>
                </div>
                <div id='containerTbProducts'>
                    <table id='tbProduct'>
                        <thead>
                            <tr>
                                <th id='tbProductTh'>Produto</th>
                                <th id='tbProductTh'>Valor/Kg</th>
                                <th id='tbProductTh'>Posto de Coleta</th>
                            </tr>
                        </thead>
                        <tbody>
                        {mktProducts.map((p, index) => { 
                            return(
                            <tr id='tbProductTr' style={{'backgroundColor': (index % 2 === 0) ?  '#F3F6F6' : '#FFF'}} key={p.idMktProduct}>
                                <td id='tbProductTd'>{p.product}</td>
                                <td id='tbProductTd'>{p.value}</td>
                                <td id='tbProductTdPosto'>{p.nmFantazy}</td>
                            </tr>
                             ); 
                        },)}
                        </tbody>
                    </table>
                    <div id='paginator'>
                        <div id='div-arrow-left'> {(page <= 0) ?
                            <img src={require('../../assets/imgs/seta-left-opac.svg')} alt='seta-left' /> :
                            <img src={require('../../assets/imgs/seta-left.svg')} alt='seta-left'
                                      onClick={() => leftPage(page)}/>}
                        </div>
                            <div id='numbersPages'>{pagesSegment.map((p) => {
                                return(<div key={p} id='numPage' onClick={() => changePage(p)}
                                   style={{backgroundColor: (p === page + 1) ?
                                   '#DDD': 'transparent'}}>{p}
                                   </div>)})}
                            </div>
                        <div id='div-arrow-right'>{(page + 1 >= totalPages) ?
                            <img src={require('../../assets/imgs/seta-right-opac.svg')} alt='seta-right' /> :
                            <img src={require('../../assets/imgs/seta-right.svg')} alt='seta-right'
                                      onClick={() => rightPage(page)}/>}
                        </div>
                    </div>
                    {/* <Paginator  page={this.state.page} totalPages={this.state.totalPages} pages={this.state.pages} segment={this.state.pagesSegment} loadPages={this.loadPages}/> */}
                    
                </div>
            </div>
        )
    
}
export default Product;
