import React, {Component} from 'react';
import './App.css';

class App extends Component() {

  state = {
    name : ''
  }

  render(){
    return (
      <div>
        <label>Nome: </label>
        <input type='text'
          value={this.state.name}
          onChange={(e) => this.setState({name: e.target.value})} 
        />
      </div>
    )
  }
}

export default App;
