import React, {useEffect, useState} from 'react';
import {Bar} from 'react-chartjs-2'; 

import './home.css';
import { axiosService } from '../../service/axios';

function Home() {

    const [options, setOptions] = useState({
        title: 'Gráfico de Pizza'
    })
    const [data, setData] = useState([])
    const [state, setState] = useState({
        labels:[],
        datasets:[{
            label:'',
            data:[],
            backgroundColor: '',
            borderColor: '',
            borderWidth: '',
        }]
    })

    
    useEffect(() => {
        loadDashProductValue();
    },[]);

    async function loadDashProductValue() {
        axiosService({
            method:'GET',
            url:'/recycling/mktProductValue',
            params:{
                page:0,
                size:5
            }
        }).then(response => {

            formatGraficValue(response);
            
        }).catch(error => {

        })
    };


    function formatGraficValue(response) {
        let resp = [];
        let values = [];
        let products = [];
        resp = response.data.listRecyclingProductsValue;
            
        console.log(resp)
        resp.map(p => {
            values.push(parseFloat(p.averageValue.replace(',', '.')));
            products.push(p.product.product)         
        })
        setData(products);
        console.log('produtos: ' + products);

        setState({
            labels:products,
            datasets:[{
                label: 'valor',
                backgroundColor: '#DDDF4B',
                data:values
            }]
        })
    }


        
    return(
        <div id='containerPage'>
            <div id='containerResultFind'>
                
            <Bar
                data={state}
                options={{
                    title:{
                    display:true,
                    text:'Produtos com maior valor pago por Kg',
                    fontSize:20
                    },
                    legend:{
                    display:true,
                    position:'right'
                    },
                    
                    tooltips: {
                        callbacks: {
                            label: function(tooltipItem, data) {
                                debugger;
                                var value = data.datasets[tooltipItem.datasetIndex].value || '';
            
                                
                                return 'R$' + value.replace('.', ',').toFixed(2);
                            }
                        }
                    }
                    
                }}
            />
                
            </div>
        </div>
    )
    
}
export default Home;



