import React, {useState, useEffect} from 'react';

import './product.css';
import { axiosService } from '../../service/axios';
import Paginator from '../../components/Paginator';

function Product(props)  {
    
    const [products, setProducts] = useState([]);
    const [mktProducts, setMktProducts] = useState([]);
    const [sort, setSort] = useState();
    const [page, setPage] = useState(0);
    const [size] = useState(1);
    const [idProduct, setIdProduct] = useState();
    const [totalPages, setTotalPages] = useState();
    const [refreshPag, setRefreshPag] = useState(false);
        

    useEffect(() => {
       loadProducts();
       loadMktProducts();
    },[page]);


    async function loadProducts() {
        try {
            const response = await axiosService({
                url: '/product/find',
                method:'GET',
                params: {
                    idProduct: '',
                    page: 0,
                    size: '0',
                    sort: '',
                    sortDirection: ''
                }
            })
            console.log(response);
            setProducts(response.data.response);
        } catch (error) {
            console.log(error.message);
        }
    }

    async function loadMktProducts(pageThis){
        debugger;
        try {
            const response = await axiosService({
                url: '/marketProduct/find', method:'GET',
                params: {
                    productId: idProduct,
                    page: (pageThis === undefined) ? page : pageThis,
                    size: size,
                    sort: sort,
                    sortDirection: ''
                }
            })
            debugger;
            console.log(response)
            setMktProducts(response.data.response);
            setTotalPages(response.data.totalPages);
        } catch (error) {
            console.log(error.message);
        }
    }

    function sortFind(e) {
        setSort(e.target.value)
    }

    function productFind(e) {
        setIdProduct(e.target.value)
    }

    function find() {
        console.log(idProduct);
        console.log(sort);
        setPage(0)
        loadMktProducts(0);
        setRefreshPag(false);
    }

    function onPaginatorChanged(page) {
        setPage(page)
        loadMktProducts(page);
    }

    return(
        <div id='containerProducts'>
            <div id='titleProducts'>Produtos por posto de coleta</div>
            <div id='containerFind'>
                <div id='containerSelect'>
                    <label id='labelSelectFind'>Produto: </label>
                    <select id='selectFind' name='idProduct' value={idProduct} onChange={productFind}>
                                <option id='selectFindOpt' key={0} value=''>Selecione...</option>
                        {products.map((p) => {
                            return(
                                <option id='selectFindOpt' key={p.id} value={p.id}>{p.product}</option>
                            )
                        })}
                    </select>
                </div>
                <div id='containerSelect'>
                    <label id='labelSelectFind'>Ordem: </label>
                    <select id='selectFind' name='sort' value={sort} onChange={sortFind}>
                        <option id='selectFindOpt' value=''>Selecione...</option>
                        <option id='selectFindOpt' value='Valor'>Valor</option> 
                        <option id='selectFindOpt' value='Produto'>Produto</option>
                        <option id='selectFindOpt' value='Posto'>Posto de coleta</option>
                    </select>
                </div>
                <button id='btnFindProduct' onClick={find}>Buscar</button>
            </div>
            <div id='containerTbProducts'>
                <table id='tbProduct'>
                    <thead>
                        <tr>
                            <th id='tbProductTh'>Produto</th>
                            <th id='tbProductTh'>Valor/Kg</th>
                            <th id='tbProductTh'>Posto de Coleta</th>
                        </tr>
                    </thead>
                    <tbody>
                    {mktProducts.map((p, index) => { 
                        return(
                        <tr id='tbProductTr' style={{'backgroundColor': (index % 2 === 0) ?  '#F3F6F6' : '#FFF'}} key={p.idMktProduct}>
                            <td id='tbProductTd'>{p.product}</td>
                            <td id='tbProductTd'>{p.value}</td>
                            <td id='tbProductTdPosto'>{p.nmFantazy}</td>
                        </tr>
                        ); 
                    },)}
                    </tbody>
                </table>
           
                {(totalPages !== undefined || refreshPag === true) &&
                    <Paginator id='paginator' page={page} totalPages={totalPages} lengthPages={3} callbackParent={(page) => onPaginatorChanged(page)} />
                }
                
            </div>
        </div>
    )  
    
}
export default Product;
