import React, {Component, useState} from 'react';

import './paginator.css';

class Paginator extends Component() {

    constructor(props) {
        super();
        this.state = {          
            pages:[],
            pagesSegment:[],   
            page: 0,
            totalPages: '',
        }
        
        this.loadPages = this.loadPages.bind(this);
        this.changePage = this.changePage.bind(this);
        this.leftPage = this.leftPage.bind(this);
        this.rightPage = this.rightPage.bind(this);
    }

    componentDidMount() {
        this.setState({pages : this.props.pages})
    }

    loadPages() {
        debugger;
        let pages = [];
        let segment = [];
        let pg = this.state.page + 1;
        for(let i = 1; i < this.state.totalPages + 1; i++) {
            pages.push(i);
        }
        segment = pages.slice(pg-2 >= 0 ? pg-2 : 0, pg+2 <= pages.length ? pg+2 : pages.length);     
        this.setState({pages: pages})
        this.setState({pagesSegment: segment})
    }
    
    changePage(page){
        debugger;
        let segment = [];
        let pages = this.state.pages;
        let quantExibPages = 3;
        let init = (page - (quantExibPages-1) >= 0) ? page - (quantExibPages-1) : 0;
        let limit = (init + quantExibPages < pages.length) ? init + quantExibPages : pages.length;
        console.log('pages.length: ' + pages.length)
        console.log(page)
        page = page - 1;
        page = (page > this.state.totalPages) ? this.state.totalPages-1 : page;
        page = (page <= 0) ? 0 : page;
        segment = pages.slice(init, limit);
        if(limit === pages.length && (limit - quantExibPages) >= 0) {
            segment = pages.slice(limit-quantExibPages, limit);
        }
        this.setState({page: page});     
        this.setState({pagesSegment: segment})
    }

    leftPage(page) {
        console.log(page);
        if(page - 1 >= 0) {
            let nextPage = page;
            this.changePage(nextPage)
        }
    }

    rightPage(page) {
        console.log(page);
        debugger;
        if(page + 2 > 0 || page + 2 < this.state.pages.length) {
            let nextPage = page + 2;
            this.changePage(nextPage)
        }
    }
    
   render() {
    return(
        <div id='paginator'>
            <div id='div-arrow-left'> {(this.state.page <= 0) ?
                <img src={require('../../assets/imgs/seta-left-opac.svg')} alt='seta-left' /> :
                <img src={require('../../assets/imgs/seta-left.svg')} alt='seta-left'
                            onClick={() => this.leftPage(this.state.page)}/>}
            </div>
                <div id='numbersPages'>{this.state.pagesSegment.map((p) => {
                    return(<div key={p} id='numPage' onClick={() => this.changePage(p)}
                        style={{backgroundColor: (p === this.state.page + 1) ?
                        '#DDD': 'transparent'}}>{p}
                        </div>)})}
                </div>
            <div id='div-arrow-right'>{(this.state.page + 1 >= this.state.totalPages) ?
                <img src={require('../../assets/imgs/seta-right-opac.svg')} alt='seta-right' /> :
                <img src={require('../../assets/imgs/seta-right.svg')} alt='seta-right'
                            onClick={() => this.rightPage(this.state.page)}/>}
            </div>
        </div>
    )
   }
}
export default Paginator;
