import React, {Component} from 'react';

import './product.css';
import { axiosService } from '../../service/axios';
import Paginator from '../../components/Paginator';

function Product()  {
    
        this.state = {
            products:[],
            mktProducts:[],
            pages:[],
            pagesSegment:[],
            sort: '',
            page: 0,
            size: 1,
            idProduct: 0,
            totalPages: '',
            totalElements: '',
            conuntLinesTable: 0,
        }
        

    function componentDidMount() {
       this.loadProducts();
       this.loadMktProducts();
       let pages = [];
       for(let i = 1; i < this.state.totalPages + 1; i++) {
           pages.push(i);
       }
       this.loadPages();
       this.setState({pages : pages})
    }

    function loadProducts() {
        try {
            const response = axiosService({
                url: '/product/find',
                method:'GET',
                params: {
                    idProduct: '',
                    page: 0,
                    size: '0',
                    sort: '',
                    sortDirection: ''
                }
            })
            console.log(response);
            this.setState({products: response.data.response});
        } catch (error) {
            console.log(error.message);
        }
    }

    function loadMktProducts(){
        try {
            const response = axiosService({
                url: '/marketProduct/find', method:'GET',
                params: {
                    idProduct: this.state.idProduct,
                    page: this.state.page,
                    size: this.state.size,
                    sort: this.state.sort,
                    sortDirection: ''
                }
            })
            console.log(response)
            this.setState({mktProducts: response.data.response});
            this.setState({totalElements: response.data.totalElements});
            this.setState({totalPages: response.data.totalPages});
        } catch (error) {
            console.log(error.message);
        }
        this.loadPages();
    }

    function sortFind(e) {
        this.setState({sort: e.target.value})
    }

    function productFind(e) {
        this.setState({idProduct: e.target.value})
    }

    function find() {
        console.log(this.state.idProduct);
        console.log(this.state.sort);
    }

    function loadPages() {
        debugger;
        let pages = [];
        let segment = [];
        let pg = this.state.page + 1;
        for(let i = 1; i < this.state.totalPages + 1; i++) {
            pages.push(i);
        }

        segment = pages.slice(pg-2 >= 0 ? pg-2 : 0, pg+2 <= pages.length ? pg+2 : pages.length);
        
        this.setState({pages: pages})
        this.setState({pagesSegment: segment})

    }

    function changePage(page){
        debugger;
        let segment = [];
        let pages = this.state.pages;
        let quantExibPages = 3;
        let init = (page - (quantExibPages-1) >= 0) ? page - (quantExibPages-1) : 0;
        let limit = (init + quantExibPages < pages.length) ? init + quantExibPages : pages.length;
        console.log('pages.length: ' + pages.length)
        console.log(page)
        page = page - 1;
        page = (page > this.state.totalPages) ? this.state.totalPages-1 : page;
        page = (page <= 0) ? 0 : page;
        segment = pages.slice(init, limit);
        if(limit === pages.length && (limit - quantExibPages) >= 0) {
            segment = pages.slice(limit-quantExibPages, limit);
        }
        this.setState({page: page});     
        this.setState({pagesSegment: segment})
    }

    function leftPage(page) {
        console.log(page);
        if(page - 1 >= 0) {
            let nextPage = page;
            this.changePage(nextPage)
        }
    }

    function rightPage(page) {
        console.log(page);
        debugger;
        if(page + 2 > 0 || page + 2 < this.state.pages.length) {
            let nextPage = page + 2;
            this.changePage(nextPage)
        }
    }

 
       
        return(
            <div id='containerProducts'>
                <div id='titleProducts'>Produtos por posto de coleta</div>
                <div id='containerFind'>
                    <div id='containerSelect'>
                        <label id='labelSelectFind'>Produto: </label>
                        <select id='selectFind' name='idProduct' value={this.state.idProduct} onChange={this.productFind}>
                                    <option id='selectFindOpt' key={0} value=''>Selecione...</option>
                            {this.state.products.map((p) => {
                                return(
                                    <option id='selectFindOpt' key={p.id} value={p.id}>{p.product}</option>
                                )
                            })}
                        </select>
                    </div>
                    <div id='containerSelect'>
                        <label id='labelSelectFind'>Ordem: </label>
                        <select id='selectFind' name='sort' value={this.state.sort} onChange={this.sortFind}>
                            <option id='selectFindOpt' value=''>Selecione...</option>
                            <option id='selectFindOpt' value='Valor'>Valor</option> 
                            <option id='selectFindOpt' value='Produto'>Produto</option>
                            <option id='selectFindOpt' value='Posto'>Posto de coleta</option>
                        </select>
                    </div>
                    <button id='btnFindProduct' onClick={this.find}>Buscar</button>
                </div>
                <div id='containerTbProducts'>
                    <table id='tbProduct'>
                        <thead>
                            <tr>
                                <th id='tbProductTh'>Produto</th>
                                <th id='tbProductTh'>Valor/Kg</th>
                                <th id='tbProductTh'>Posto de Coleta</th>
                            </tr>
                        </thead>
                        <tbody>
                        {this.state.mktProducts.map((p, index) => { 
                            return(
                            <tr id='tbProductTr' style={{'backgroundColor': (index % 2 === 0) ?  '#F3F6F6' : '#FFF'}} key={p.idMktProduct}>
                                <td id='tbProductTd'>{p.product}</td>
                                <td id='tbProductTd'>{p.value}</td>
                                <td id='tbProductTdPosto'>{p.nmFantazy}</td>
                            </tr>
                             ); 
                        },)}
                        </tbody>
                    </table>
                    <div id='paginator'>
                        <div id='div-arrow-left'> {(this.state.page <= 0) ?
                            <img src={require('../../assets/imgs/seta-left-opac.svg')} alt='seta-left' /> :
                            <img src={require('../../assets/imgs/seta-left.svg')} alt='seta-left'
                                      onClick={() => this.leftPage(this.state.page)}/>}
                        </div>
                            <div id='numbersPages'>{this.state.pagesSegment.map((p) => {
                                return(<div key={p} id='numPage' onClick={() => this.changePage(p)}
                                   style={{backgroundColor: (p === this.state.page + 1) ?
                                   '#DDD': 'transparent'}}>{p}
                                   </div>)})}
                            </div>
                        <div id='div-arrow-right'>{(this.state.page + 1 >= this.state.totalPages) ?
                            <img src={require('../../assets/imgs/seta-right-opac.svg')} alt='seta-right' /> :
                            <img src={require('../../assets/imgs/seta-right.svg')} alt='seta-right'
                                      onClick={() => this.rightPage(this.state.page)}/>}
                        </div>
                    </div>
                    <Paginator  page={this.state.page} totalPages={this.state.totalPages} pages={this.state.pages} segment={this.state.pagesSegment} loadPages={this.loadPages}/>
                    
                </div>
            </div>
        )
    
}
export default Product;
