import React, {Component} from 'react';

import { axiosService } from '../../service/axios';

class StationDetail extends Component{

    constructor(props) {
        super();
        this.state = {
            id:'',
            station: {},
            phones: [],
            emails: [],
            webContacts: [],
        }
        this.loadInfoStation = this.loadInfoStation.bind(this);
        this.formatContacts = this.formatContacts.bind(this);
    }

    
    componentDidMount(){
        const { id } = this.props.match.params;
        this.setState({id: id});
        this.loadInfoStation(id);
        console.log(id)
    }

   


    async loadInfoStation(id) {
        try {
            const response = await axiosService({
                url: '/stations/find',
                method: 'GET',
                params: {
                    page: 0,
                    size: 1,
                    idStation: id
                }
            })
            console.log(response.data.response)
            response.data.response.map(s => {
                this.setState({station: s})
                this.formatContacts(s);
            })
            console.log(this.state.station)
        } catch(error) {

        }
    }

    formatContacts(station) {
        let phones = [];
        let emails = [];
        let webContacts = [];
        if (this.state.station.phone !== null && this.state.station.phone !== '') {
            phones.push(this.state.station.phone)
        }
        if (this.state.station.phoneAdd !== null && this.state.station.phoneAdd !== '') {
            phones.push(this.state.station.phoneAdd)
        }
        if (this.state.station.cellPhone !== null && this.state.station.cellPhone !== '') {
            phones.push(this.state.station.cellPhone)
        }
        if (this.state.station.cellPhoneAdd !== null && this.state.station.cellPhoneAdd !== '') {
            phones.push(this.state.station.cellPhoneAdd)
        }
        if (this.state.station.email !== null && this.state.station.email !== '') {
            emails.push(this.state.station.email)
        }
        if (this.state.station.emailAdd !== null && this.state.station.emailAdd !== '') {
            emails.push(this.state.station.emailAdd)
        }
        if (this.state.station.site !== null && this.state.station.site !== '') {
            webContacts.push(this.state.station.site)
        }
        if (this.state.station.facebook !== null && this.state.station.facebook !== '') {
            webContacts.push(this.state.station.facebook)
        }
        if (this.state.station.otherUrl !== null && this.state.station.otherUrl !== '') {
            webContacts.push(this.state.station.otherUrl)
        }
        this.setState({phones: phones})
        this.setState({emails: emails})
        this.setState({webContacts: webContacts})
    }


    render(){
        return(
            <div id='containerPage'>
            <div id='titlePage'>{this.state.station.nmFantazy}</div>
            {/* {(message !== '' && message !== undefined) && <Message />} */}
            <div id='containerResultFind'>
                <div id='ContainerFindResultColor'>

                    <div id='stationDetail'>
                        <div><strong>Informações gerais {this.state.station.nmFantazy}</strong></div>
                        <div>
                            <strong>Endereço:</strong><br/>
                            <label>{this.state.station.street}, {this.state.station.number} {this.state.station.complement}</label><br/>
                            <label>{this.state.station.neighborhood} - {this.state.station.city} - {this.state.station.state}/{this.state.station.uf} - {this.state.station.zip}</label><br/>
                        </div>
                        <div>
                            <strong>Contatos:</strong><br/>
                            <label>Telefone: </label>
                            {this.state.phones.size !== 0 && this.state.phones.map((phone, index) => {
                                return(<label key={index}>{phone} </label>)
                            })}
                            <br/><label>E-mail: </label>
                            {this.state.emails.size !== 0 && this.state.emails.map((email, index) => {
                                return(<label key={index}>{email} </label>)
                            })}
                            
                        </div>
                    </div>
                </div>

            </div>

            </div>
        )
    }

}

export default StationDetail;
