import React, {useState, useEffect} from 'react';

import './station.css';
import '../../style.css';
import { axiosService } from '../../service/axios';
import Paginator from '../../components/Paginator';
import MessageLine from '../../components/MessageLine';

export default function Station(props) {

    const [stations, setStations] = useState([])
    const [states, setStates] = useState([])
    const [cities, setCities] = useState([])
    const [stationId, setStationId] = useState([])
    const [sort, setSort] = useState();
    const [page, setPage] = useState(0);
    const [size] = useState(5);
    const [totalPages, setTotalPages] = useState();
    const [{message, typeMessage}, setMessage] = useState({});


    useEffect(() => {
        loadStations(0);
        loadStates();
        loadCities();
    },[]);

    async function loadStations(thisPage){
        console.log(page);
        try{
            const response = await axiosService({
                url: '/stations/find',
                method: 'GET',
                params: {
                    page: (thisPage !== 0) ? thisPage: page,
                    size: size,
                    sort: sort,
                    stationId: stationId,
                }
            })
            setStations(response.data.response);
            setTotalPages(response.data.totalPages);
            setMessage({});
        } catch(error) {
            if (error.message.includes('404')) {
                setMessage({message: 'Nenhum posto de coleta foi encontrado com os parâmetros de busca informados.',
                typeMessage: 'warning'})
            } else {
                setMessage({message: 'Falha ao consultar postos de coleta.',
                typeMessage: 'error'})
            }   
        }
    }

    async function loadStates() {
        try{
            const response = await axiosService({url: '/stations/findStates', method: 'GET'})
            setStates(response.data);
        } catch(error) {
        }
    }

    async function loadCities() {
        try{
            const response = await axiosService({url: '/stations/findCities', method: 'GET'})
            setCities(response.data);
        } catch(error) {
        }
    }

    function Message(){
        if(message === null || message === '' ) return;
        return(
            <MessageLine message={message} typeMessage={typeMessage}/>
        )
    }

    function Paginar() {    
        return(
            <Paginator id='paginator' page={page} totalPages={totalPages}
            lengthPages={4} callbackParent={(page) => onPaginatorChanged(page)} />
        )
    }

    function onPaginatorChanged(page) {
        setPage(page)
        loadStations(page);
    }

    return(
        <div id='containerPage'>
            <div id='titlePage'>Postos de coleta</div>
            {(message !== '' && message !== undefined) && <Message />}
            <div id='containerFind'>
            </div>
            <div id='containerResultFind'>
                <div id='resultFindDetail'>
         
                <div id='containerFind'>
                    <div id='containerSelect'>
                        <label id='labelSelectFind'>Estado: </label>
                        <select id='selectFind' name='state' value={state} onChange={() => {}}>
                                    <option id='selectFindOpt' key={0} value=''>Selecione...</option>
                            {states.map((state, index) => {
                                return(
                                    <option id='selectFindOpt' key={index} value={state}>{state}</option>
                                )
                            })}
                        </select>
                    </div>
                    <div id='containerSelect'>
                        <label id='labelSelectFind'>Cidade: </label>
                        <select id='selectFind' name='city' value={city} onChange={() => {}}>
                                    <option id='selectFindOpt' key={0} value=''>Selecione...</option>
                            {cities.map((city, index) => {
                                return(
                                    <option id='selectFindOpt' key={index} value={city}>{city}</option>
                                )
                            })}
                        </select>
                    </div>
                    <div id='containerSelect'>
                        <label id='labelSelectFind'>Ordem: </label>
                        <select id='selectFind' name='sort' value={sort} onChange={sortFind}>
                            <option id='selectFindOpt' value=''>Selecione...</option>
                            <option id='selectFindOpt' value='Valor'>Valor</option> 
                            <option id='selectFindOpt' value='Produto'>Produto</option>
                            <option id='selectFindOpt' value='Posto'>Posto de coleta</option>
                        </select>
                    </div>
                    <button id='btnFindProduct' onClick={find}>Buscar</button>
                </div>

                <table id='tbFind'>
                    <thead>
                        <tr>
                            <th id='tbFindTh'>Posto</th>
                            <th id='tbFindTh'>Estado</th>
                            <th id='tbFindTh'>Cidade</th>
                            <th id='tbFindTh'>Bairro</th>
                            <th id='tbFindTh'>Ação</th>
                        </tr>
                    </thead>
                    <tbody>
                    {stations.map((s, index) => { 
                        return(
                        <tr id='tbFindTr' style={{'backgroundColor': (index % 2 === 0) ?  '#F3F6F6' : '#FFF'}} key={s.IdStations}>
                            <td id='tbFindTd25PorCento'>{s.nmFantazy}</td>
                            <td id='tbFindTd'>{s.state}</td>
                            <td id='tbFindTd'>{s.city}</td>
                            <td id='tbFindTd25PorCento'>{s.neighborhood}</td>
                            <td id='tbFindTd'><img id='tbIcon' style={{cursor: 'pointer'}}
                                src={require('../../assets/incons/icon-search.svg')} alt='icon-seach'/></td>
                        </tr>
                        ); 
                    },)}
                    </tbody>
                </table>
                </div>
            </div>
        </div>
    )
    
}
