import React, {useEffect, useState} from 'react';
import { Chart } from "react-google-charts";

import './home.css';
import { axiosService } from '../../service/axios';

function Home() {

    const [options, setOptions] = useState({
        title: 'GrÃ¡fico de Pizza'
    })
    const [data, setData] = useState([])
    // const [data, setData] = useState([
    //     ['Linguagens', 'Quantidade'],
    //     ['React', 100],
    //     ['Angula', 80],
    //     ['Vue', 50],
    // ])
    
    useEffect(() => {
        loadDashProductValue();
    },[])

    async function loadDashProductValue() {
        axiosService({
            method:'GET',
            url:'/recycling/mktProductValue',
            params:{
                page:0,
                size:5
            }
        }).then(response => {
            let values = [];
            let products = [['Produtos', 'Valor']];
            let resp = [];
            resp = response.data.listRecyclingProductsValue;
            
            console.log(resp)
            resp.map(p => {
                products.push([
                    p.product.product,
                    parseFloat(p.averageValue.replace(',', '.'))
                ]);          
            })
            setData(products);
            console.log('produtos: ' + products);
        }).catch(error => {

        })
    }
        
    return(
        <div id='containerPage'>
            <div id='containerResultFind'>
                
                    <Chart
                        width='300px'
                        height='200px'
                        chartType="BarChart"
                        data={data}
                        options={options, {
                            // chartArea: { width: '100%' },
                            // chartArea: { width: '50%', height: '70%' },
                            title: 'Produtos com maior valor ofertado.',
                            width: '200px',
                            height: '200px',
                            // responsive: true,
                            bar: { groupWidth: '50%' },
                            colors: ['#DDDF4B', '#2B4748'],
                            // titlePosition: 'in',
                            // axisTitlesPosition: 'in',
                            // hAxis: {textPosition: 'in'}, vAxis: {textPosition: 'in'},    
                            legend: {position: 'bottom', textStyle: {color: '#2B4748', fontSize: 10}},
                            hAxis: {format: '#,###R$'},
                            // hAxis: {format: 'currency',},
                            // legend: {display: false}
                            // legend: { width: '10%' },
                            // isStacked: true,
                            
                        }}
                        rootProps={{ 'data-testid': '3' }}
                    />
                
            </div>
        </div>
    )
    
}
export default Home;
