import React, {Component} from 'react';
import {Link} from 'react-router-dom';
import './login.css';
import axios from 'axios';

class Login extends Component {
    constructor(props) {
        super();
        this.state = {
            form: {
                email: '',
                password: ''
            }
        }
        this.insert = this.insert.bind(this);
        this.logar = this.logar.bind(this);
    }

    insert(e) {
        let form = this.state.form;
        form[e.target.name] = e.target.value;
        this.setState({form: form});
        e.preventDefault();
    }

    logar(e) {
        let url = 'http://localhost:9000/auth';
        let form = this.state.form;
        console.log(form);
        axios.post(url + '/token', form)
        .then((resp) => {})
        // fetch(url+'/token', {method: 'POST', headers:{'content-type' : 'application/json'},
        // body: {LoginDto:{email: "form.email", password: "form.password"}}})
        // .then((resp) => resp.json())
        // .then((json) => {
        //     console.log(json)
        // })
        // fetch(url+'/dsjfhksj', {method: 'GET'})
        // .then((resp) => resp.json())
        // .then((json) => {
        //     console.log(json)
        // })
        e.preventDefault();
    }


    
    render(){
        return(
            <div id='formContainer'>
                <form onSubmit={this.logar} id='login'>
                    <label>E-mail:</label>
                    <input type='text' autoComplete='off' autoFocus value={this.state.form.email}
                    onChange={this.insert} placeholder='email@email.com.br' name='email' />
                    <label>Senha:</label>
                    <input type='password' autoComplete='off' value={this.state.form.password}
                    onChange={this.insert} placeholder='Sua senha'  name='password'/>
                    <button>Entrar</button>
                    <div id='formCriarConta'>
                        <Link to='/conta'>Criar conta gratuita</Link>
                    </div>
                </form>
            </div>
        )
    }
}

export default Login;
