import React, {Component} from 'react';

import './login.css';

class Login extends Component {
    constructor(props) {
        super();
        this.state = {
            form: {
                email: '',
                password: ''
            }
        }
        this.logar = this.logar.bind(this);
    }

    logar(e) {
        let form = this.state.form;
        form[e.target.name] = e.target.value;
        this.setState({form: form});
    }
    
    render(){
        return(
            <div id='formContainer'>
                <form id='login'>
                    <label>E-mail:</label><br/>
                    <input type='text' autoComplete='off' autoFocus value={this.state.form.email}
                    onChange={this.logar} placeholder='email@email.com.br' /> <br/>
                    <label>Senha:</label><br/>
                    <input type='text' autoComplete='off' value={this.state.form.password}
                    onChange={this.logar} placeholder='Sua senha' /> <br/>
                    <button>Entrar</button><br/>
                    <p>Criar conta gratuita</p><br/>
                </form>
            </div>
        )
    }
}

export default Login;
