import React, {useEffect, useState} from 'react';
import {Bar, Doughnut} from 'react-chartjs-2';

import './home.css';
import { axiosService } from '../../service/axios';

function Home() {

    const [tooltipsNumber, setTooltipsNumber] = useState({})
    const [tooltipsAmount, setTooltipsAmount] = useState({})
    const [dataValue, setDataValue] = useState({labels:[], datasets:[{}]})
    const [dataMaxValue, setDataMaxValue] = useState({labels:[], datasets:[{}]})
    const [dataMaxSize, setDataMaxSize] = useState({labels:[], datasets:[{}]})


    
    useEffect(() => {
        loadDashProductValue();
        loadDashProductSize();
    },[]);

    async function loadDashProductValue() {
        axiosService({
            method:'GET',
            url:'/recycling/mktProductValue',
            params:{
                page:0,
                size:5
            }
        }).then(response => {
            let resp = [];
            let maxValues = [];
            let minValues = [];
            let averageValues = [];
            let products = [];
            console.log(response)
            resp = response.data.listRecyclingProductsValue;          
            resp.forEach((p) => {
                maxValues.push(parseFloat(p.maxValue.replace(',', '.')));
                minValues.push(parseFloat(p.minValue.replace(',', '.')));
                averageValues.push(parseFloat(p.averageValue.replace(',', '.')));
                products.push(p.product.product)         
            })
            setDataMaxValue({
                labels:products,
                datasets:[{label: 'valor máximo/Kg', backgroundColor: '#B9C400',data:maxValues}]
            })
            setDataValue({
                labels:products,
                datasets:[
                    {label: 'valor mínimo/Kg', backgroundColor: '#BADFD7',data:minValues},
                    {label: 'valor máximo/Kg', backgroundColor: '#B9C400',data:maxValues},
                    {label: 'valor médio/Kg', backgroundColor: '#B9C598',data:averageValues},
                ]
            })
            formatToolTips('amount');           
        }).catch(error => {
        })
    };

    async function loadDashProductSize() {
        axiosService({
            method:'GET',
            url:'/recycling/mktProductSize',
            params:{
                page:0,
                size:5
            }
        }).then(response => {
            let resp = [];
            let values = [];
            let products = [];
            console.log(response)
            resp = response.data.listRecyclingProductsValue;          
            resp.forEach((p) => {
                values.push(p.countMktProducts);
                products.push(p.product.product)         
            })
            setDataMaxSize({
                labels:products,
                datasets:[{
                    label: 'quantidade',
                    borderWidth:0,
                    backgroundColor: ['#547F82', '#5B7813', '#00AB97','#B9C400'],
                    data:values
                }]
            })
            formatToolTips('number');            
        }).catch(error => {
        })
    };


    function formatToolTips(type){
        console.log(type)
        if(type === 'amount') {
            setTooltipsAmount({
                callbacks: {            
                    label: function(tooltipItem, data) {
                        var unit = 'R$ ';
                        // var label = data.datasets[tooltipItem.datasetIndex].label;
                        // label = (label) ? label + ': ' + unit + ' ' : ''
                        return unit + tooltipItem.value.toString().replace('.', ',');
                    }
                }
            })
        }
        if(type === 'number') {
            setTooltipsNumber({
                callbacks: {            
                    label: function(tooltipItem, data) {
                        return tooltipItem.value.toString();
                    }
                }
            })
        }
    }


        
    return(
        <div id='containerPage'>
            <div id='containerResultFind'>
                <div style={{display:'flex', flexWrap: 'wrap', alignItems: 'center', justifyContent:'space-between'}}>
                    <div style={{margin: '10px', padding:'20px', backgroundColor: '#F3F6F6', borderRadius: '5px'}}>
                    <div style={{display:'flex', alignItems: 'center', justifyContent: 'center', width:'100%'}}><strong style={{color: '#2B4748', fontSize: '14px', textAlign:'center'}}>Produtos com maior valor pago por Kg</strong></div>
                        <Bar
                            height={250}
                            width={250}
                            data={dataMaxValue}
                            options={{
                                title:{display:true,text:'',fontSize:14},
                                legend:{display:true, position:'bottom', labels:{boxWidth:15}},     
                                tooltips: tooltipsAmount    
                            }}
                        />
                    </div>
                    
                    <div style={{margin: '10px', padding:'20px', backgroundColor: '#F3F6F6', borderRadius: '5px'}}>
                        <div style={{display:'flex', alignItems: 'center', justifyContent: 'center', width:'100%'}}><strong style={{color: '#2B4748', fontSize: '14px', textAlign:'center'}}>Produtos com mais anunciados.</strong></div>
                        <Bar
                            height={250}
                            width={250}
                            data={dataValue}
                            options={{
                                title:{display:true,text:'',fontSize:14},
                                legend:{display:true, position:'bottom', labels:{boxWidth:15}},     
                                tooltips: tooltipsNumber     
                            }}
                        />
                    </div>
                </div>
                <div style={{display:'flex', flexWrap: 'wrap', alignItems: 'center', justifyContent:'space-between'}}>
                    <div style={{margin: '10px', padding:'20px', backgroundColor: '#F3F6F6', borderRadius: '5px'}}>
                    <div style={{display:'flex', alignItems: 'center', justifyContent: 'center', width:'100%'}}><strong style={{color: '#2B4748', fontSize: '14px', textAlign:'center'}}>Produtos com maior valor pago por Kg</strong></div>
                        <Bar
                            height={250}
                            width={250}
                            data={dataMaxValue}
                            options={{
                                title:{display:true,text:'',fontSize:14},
                                legend:{display:true, position:'bottom', labels:{boxWidth:15}},     
                                tooltips: tooltipsAmount    
                            }}
                        />
                    </div>
                    <div style={{margin: '10px', padding:'20px', backgroundColor: '#F3F6F6', borderRadius: '5px'}}>
                        <div style={{display:'flex', alignItems: 'center', justifyContent: 'center', width:'100%'}}><strong style={{color: '#2B4748', fontSize: '14px', textAlign:'center'}}>Produtos com mais anunciados.</strong></div>
                        <Doughnut
                            height={250}
                            width={250}
                            data={dataMaxSize}
                            options={{
                                title:{display:true,text:'',fontSize:14},
                                legend:{display:true, position:'bottom', labels:{boxWidth:15}},     
                            }}
                        />
                    </div>
                </div>     
            </div>
        </div>
    )
    
}
export default Home;



