import React, {useEffect, useState} from 'react';

import { axiosService } from '../../service/axios';

function Admin() {


    const [user, setUser] = useState({
        idUser:'', email:'', token:''
    });

    useEffect(() => {
        findUser();
    }, [])

    function findUser() {
        let user = {
            idUser: localStorage.getItem('idUser'),
            email: localStorage.getItem('email'),
            token: localStorage.getItem('token')
        }
        setUser({user});
        findStationToUser(user);
    }

    async function findStationToUser(user) {
        debugger;
        await axiosService({
            method: 'GET',
            url:'/stations/find',
            params:{email: 'user.email', page: 0, size:1}
        }).then(response => {
            console.log(response)
        }).catch(error => {
            if(error.message.includes('404')){

            }
        })
    }

    const messageRegister = () => {
        return(
            <div>
                <div>Ainda não tem um posto de coleta cadastrado? Deseja cadastrar?</div>
                <div>
                    <button>Sim</button>
                    <button>Não</button>
                </div>
            </div>
        )
    }

    return(
        <div>{user.email}</div>
    )

}
export default Admin;
