import React, {Component} from 'react';

import './product.css';
import { axiosService } from '../../service/axios';

class Product extends Component {
    constructor(props) {
        super();
        this.state = {
            products:[],
            mktProducts:[],
            pages:[],
            sort: '',
            page:0,
            size: 1,
            idProduct: 0,
            totalPages: '',
            totalElements: '',
        }
        this.loadProducts = this.loadProducts.bind(this);
        this.loadMktProducts = this.loadMktProducts.bind(this);
        this.sortFind = this.sortFind.bind(this);
        this.productFind = this.productFind.bind(this);
        this.find = this.find.bind(this);
        this.loadPages = this.loadPages.bind(this);
    }

    componentDidMount() {
       this.loadProducts();
       this.loadMktProducts();
    }

    async loadProducts() {
        try {
            const response = await axiosService({
                url: '/product/find',
                method:'GET',
                params: {
                    idProduct: '',
                    page: 0,
                    size: '0',
                    sort: '',
                    sortDirection: ''
                }
            })
            console.log(response);
            this.setState({products: response.data.response});
        } catch (error) {
            console.log(error.message);
        }
    }

    async loadMktProducts(){
        try {
            const response = await axiosService({
                url: '/marketProduct/find', method:'GET',
                params: {
                    idProduct: this.state.idProduct,
                    page: this.state.page,
                    size: this.state.size,
                    sort: this.state.sort,
                    sortDirection: ''
                }
            })
            console.log(response)
            this.setState({mktProducts: response.data.response});
            this.setState({totalElements: response.data.totalElements});
            this.setState({totalPages: response.data.totalPages});
        } catch (error) {
            console.log(error.message);
        }
        this.loadPages();
    }

    sortFind(e) {
        this.setState({sort: e.target.value})
    }

    productFind(e) {
        this.setState({idProduct: e.target.value})
    }

    find() {
        console.log(this.state.idProduct);
        console.log(this.state.sort);
    }

    loadPages() {
        debugger;
        let pages = [];
        let pagesFinal = [];
        for(let i = 1; i < this.state.totalPages + 1; i++) {
            pages.push(i);
        }

        for(let i = 1; i < this.pages.size(); i++) {
            pagesFinal.push(i);
        }

        
        this.setState({pages: pagesFinal})
        console.log(pages)
    }

    render(){
       
        return(
            <div id='containerProducts'>
                <div id='titleProducts'>Produtos por posto de coleta</div>
                <div id='containerFind'>
                    <div id='containerSelect'>
                        <label id='labelSelectFind'>Produto: </label>
                        <select id='selectFind' name='idProduct' value={this.state.idProduct} onChange={this.productFind}>
                                    <option id='selectFindOpt' key={0} value=''>Selecione...</option>
                            {this.state.products.map((p) => {
                                return(
                                    <option id='selectFindOpt' key={p.id} value={p.id}>{p.product}</option>
                                )
                            })}
                        </select>
                    </div>
                    <div id='containerSelect'>
                        <label id='labelSelectFind'>Ordem: </label>
                        <select id='selectFind' name='sort' value={this.state.sort} onChange={this.sortFind}>
                            <option id='selectFindOpt' value=''>Selecione...</option>
                            <option id='selectFindOpt' value='Valor'>Valor</option> 
                            <option id='selectFindOpt' value='Produto'>Produto</option>
                            <option id='selectFindOpt' value='Posto'>Posto de coleta</option>
                        </select>
                    </div>
                    <button id='btnFindProduct' onClick={this.find}>Buscar</button>
                </div>
                <div id='containerTbProducts'>
                    <table id='tbProduct'>
                        <thead>
                            <tr>
                                <th id='tbProductTh'>Produto</th>
                                <th id='tbProductTh'>Valor/Kg</th>
                                <th id='tbProductTh'>Posto de Coleta</th>
                            </tr>
                        </thead>
                        <tbody>
                        {this.state.mktProducts.map((p) => {
                            return(
                            <tr id='tbProductTr' style={{'backgroundColor': (p.idMktProduct%2 !== 0) ?  '#F3F6F6' : '#FFF'}} key={p.idMktProduct}>
                                <td id='tbProductTd'>{p.product}</td>
                                <td id='tbProductTd'>{p.value}</td>
                                <td id='tbProductTdPosto'>{p.nmFantazy}</td>
                            </tr>
                             );
                        })}
                        </tbody>
                    </table>
                    <div id='paginator'>
                        <div> 
                            <img src={
                                (this.state.page === 0) ?
                                 require('../../assets/imgs/seta-left-opac.svg')
                                :require('../../assets/imgs/seta-left.svg')} alt='seta-left'/>
                        </div>
                            <div>{this.state.pages.map((p) => {return(<div key={p}>{p}</div>)})}</div>
                        <div><img src={require('../../assets/imgs/seta-right.svg')} alt='seta-right'/></div>
                    </div>
                </div>
            </div>
        )
    }
}
export default Product;
