import React, {Component} from 'react';
import {Link} from 'react-router-dom';

import './login.css';

class Login extends Component {
    constructor(props) {
        super();
        this.state = {
            form: {
                email: '',
                password: ''
            }
        }
        this.logar = this.logar.bind(this);
        this.entrar = this.entrar.bind(this);
    }

    logar(e) {
        let form = this.state.form;
        form[e.target.name] = e.target.value;
        this.setState({form: form});
    }

    entrar(e){
        e.preventDefault();
    }
    
    render(){
        return(
            <div id='formContainer'>
                <form onSubmit={this.entrar} id='login'>
                    <label>E-mail:</label>
                    <input type='text' autoComplete='off'  value={this.state.form.email}
                    onChange={this.logar} placeholder='email@email.com.br' />
                    <label>Senha:</label>
                    <input type='password' autoComplete='off' value={this.state.form.password}
                    onChange={this.logar} placeholder='Sua senha' />
                    <button>Entrar</button>
                    <div id='formCriarConta'>
                        <Link to='/'>Criar conta gratuita</Link>
                    </div>
                </form>
            </div>
        )
    }
}

export default Login;
