import React, {useEffect, useState} from 'react';
import { Link } from 'react-router-dom';

import './admin.css';
import '../../style.css'
import { axiosService } from '../../service/axios';
import { existParam } from '../../utils/ValidInfo';
import MessageLine from '../../components/MessageLine';

function Admin() {

    const [stationNotFound, setStationNotFound] = useState();
    const [user, setUser] = useState({idUser:'', email:'', token:''});
    const [id, setId] = useState();
    const [stations, setStations] = useState([]);
    // const [station, setStation] = useState({phones:[], emails:[], webContacts:[], products:[]});
    const [stationsFormat, setStationsFormat] = useState([]);
    const [message, setMessage] = useState();
    const [typeMessage, setTypeMessage] = useState();
    const [load, setLoad] = useState();


    useEffect(() => {
        findUser();
    }, [])

    function findUser() {
        let user = {
            idUser: localStorage.getItem('idUser'),
            email: localStorage.getItem('email'),
            token: localStorage.getItem('token')
        }
        setUser({user});
        findStationToUser(user);
    }

    async function findStationToUser(user) {
        debugger;
        console.log(user.email)
        await axiosService({
            method: 'GET',
            url:'/stations/find',
            params:{email: user.email, page: 0, size:1}
        }).then(response => {
            console.log(response)
            setStations(response.data.response)
            let stationsFormat = [];
            response.data.response.map(station => {
                let products = loadProductsStation(station.idStation);
                let phones = formatPhones(station);
                let emails = formatEmails(station);
                let webContacts = formatWebContacts(station);
                let stationFormat = {station, products, phones, emails, webContacts}
                stationsFormat.push(stationFormat);
            });
            setStationNotFound(false);
            setLoad(true);
        }).catch(error => {
            if(error.message.includes('404')){
                setStationNotFound(true);
            }
            setMessage('Falha ao consultar informações do posto de coleta.');
            setTypeMessage('error')   
            setLoad(false)
        })
    }

    function messageRegister() {
        return(
            <div id='stationNotFound'>
                <div id='txtStationNotFound'>Ops... parece que você ainda não
                tem um posto de coleta cadastrado, deseja cadastrar agora?</div>
                <div id='btnsContainerNotFound'>
                    <Link to='/cadastrar' id='btnGenericYes'><div>Sim</div></Link>
                    <Link to='/' id='btnGenericNot'><div>Não</div></Link>
                </div>
            </div>
        )
    }

    async function loadProductsStation(id) {
        try {
            const response = await axiosService({
                url: '/marketProduct/find',
                method: 'GET',
                params: {
                    page: 0,
                    size: 0,
                    stationId: id
                }
            })
            return response.data.response;
        } catch(error) {
           
        }
    }

    function formatPhones(station) {
        let phones = [];
        existParam(station.phone) && phones.push(station.phone);  
        existParam(station.phoneAdd) && phones.push(station.phoneAdd);
        existParam(station.cellPhone) && phones.push(station.cellPhone);
        existParam(station.cellPhoneAdd) && phones.push(station.cellPhoneAdd);
        return phones;
    }

    function formatEmails(station) {
        let emails = [];        
        existParam(station.email) && emails.push(station.email);   
        existParam(station.emailAdd) && emails.push(station.emailAdd);
        return emails;
    }

    function formatWebContacts(station) {
        let webContacts = [];   
        existParam(station.site) && webContacts.push(station.site);    
        existParam(station.facebook) && webContacts.push(station.facebook);
        existParam(station.otherUrl) && webContacts.push(station.otherUrl);
        return webContacts;
    }



    return(
        <div id='containerPage'>
            <div id='titlePage'></div>
            <div id='containerResultFind'>
                <div>{stationNotFound === true && messageRegister()}</div>
                {load === false ?
                    <MessageLine message={message} typeMessage={typeMessage} /> :
                    <div id='ContainerFindResultColor'>
                        <div id='containerBtnGoBack'>
                            <div  id='btnGoBack' onClick={() => this.props.history.push('/postos')}>voltar</div>
                        </div>
                        <div id='stationDetailAdmin'>
                            <div id='titleInfo'><strong>Informações gerais</strong></div>
                            <div id='detailInfo'>
                                <strong id='subtitleInfo'>Endereço:</strong><br/>
                                <label>{this.state.station.street}, {this.state.station.number} {this.state.station.complement}</label><br/>
                                <label>{this.state.station.neighborhood} - {this.state.station.city} - {this.state.station.state}/{this.state.station.uf} - {this.state.station.zip}</label><br/>
                            </div>
                            <div id='detailInfo'>
                                <strong>Contatos:</strong><br/>
                                <label>Telefone: </label>
                                {this.state.phones.size !== 0 && this.state.phones.map((phone, index) => {
                                    return(<label key={index}>{phone} </label>)
                                })}
                                <br/><label>E-mail: </label>
                                {this.state.emails.size !== 0 && this.state.emails.map((email, index) => {
                                    return(<label key={index}>{email} </label>)
                                })}      
                                <br/><label>Website e redes sociais: </label>
                                {this.state.webContacts.size !== 0 && this.state.webContacts.map((web, index) => {
                                    return(<label key={index}>{web} </label>)
                                })}      
                            </div>
                            <div id='line'></div>
                        </div>
                    </div>
                }                
             </div>   
        </div>
    )

}
export default Admin;
