import React, {Component} from 'react';
import {Link} from 'react-router-dom';
import './login.css';
import axios from 'axios';

class Login extends Component {
    constructor(props) {
        super();
        this.state = {
            form: {
                email: '',
                password: ''
            }
        }
        this.insert = this.insert.bind(this);
        this.logar = this.logar.bind(this);
    }

    insert(e) {
        let form = this.state.form;
        form[e.target.name] = e.target.value;
        this.setState({form: form});
    }

    logar = async(e) => {
        e.preventDefault();
        try{
            let url = 'http://localhost:9000/auth';
            let form = this.state.form;
            const response = await axios.post(url + '/token', form);
            localStorage.setItem('token', response.data.type + " " + response.data.token);
            localStorage.setItem('idUser', response.data.idUser)
            localStorage.setItem('email', response.data.email)
            this.props.history.replace('/cadastrar')
        } catch (erro) {
            console.log(erro.response)
        }
    }


    
    render(){
        return(
            <div id='formContainer'>
                <form onSubmit={this.logar} id='login'>
                    <label>E-mail:</label>
                    <input type='text' autoComplete='off' autoFocus value={this.state.form.email}
                    onChange={this.insert} placeholder='email@email.com.br' name='email' />
                    <label>Senha:</label>
                    <input type='password' autoComplete='off' value={this.state.form.password}
                    onChange={this.insert} placeholder='Sua senha'  name='password'/>
                    <button>Entrar</button>
                    <div id='formCriarConta'>
                        <Link to='/conta'>Criar conta gratuita</Link>
                    </div>
                </form>
            </div>
        )
    }
}

export default Login;
