import React from 'react';
import './App.css';

function App() {

  state = {
    name : ''
  }

  return (
    <div>
      <label>Nome: </label>
      <input type='text'
        value={this.state.name}
        onChange={(e) => this.setState({nome: e.target.value})} 
      />
    </div>
  );
}

export default App;
