import React, {useEffect, useState} from 'react';

import './admin.css'
import { axiosService } from '../../service/axios';

function Admin() {

    const [stationNotFound, setStationNotFound] = useState();
    const [user, setUser] = useState({
        idUser:'', email:'', token:''
    });

    useEffect(() => {
        findUser();
    }, [])

    function findUser() {
        let user = {
            idUser: localStorage.getItem('idUser'),
            email: localStorage.getItem('email'),
            token: localStorage.getItem('token')
        }
        setUser({user});
        findStationToUser(user);
    }

    async function findStationToUser(user) {
        debugger;
        await axiosService({
            method: 'GET',
            url:'/stations/find',
            params:{email: 'user.email', page: 0, size:1}
        }).then(response => {
            console.log(response)
        }).catch(error => {
            if(error.message.includes('404')){
                setStationNotFound(false);
            }
        })
    }

    function messageRegister() {
        return(
            <div id='stationNotFound'>
                <div id='txtStationNotFound'>Ops, parece que você ainda não tem um posto de coleta cadastrado, deseja cadastrar agora?</div>
                <div id='btnsContainerNotFound'>
                    <button id='btnGenericYes'>Sim</button>
                    <button id='btnGenericNot'>Não</button>
                </div>
            </div>
        )
    }

    return(
        <div id='containerPage'>
            <div id='titlePage'></div>
            <div id='containerResultFind'>
                <div>{stationNotFound === false && messageRegister()}</div>

            </div>
        </div>
    )

}
export default Admin;
