import React, {Component, useEffect} from 'react';
import { Link } from 'react-router-dom';

import '../../style.css';
import './productEdit.css'
import { axiosService } from '../../service/axios';
import MessageLine from '../../components/MessageLine';
import Popup from '../../components/Popup';

class ProductEdit extends Component {
    constructor(props) {
        super();
        this.state = {
            id:'',
            mktProducts:[],
            stationNotFound:'',
            load:'',
            message:'',
            typeMessage:'',
            edit: false,
            line: '',
            newValue: '',
            oldValue:'',
            idMktProduct:'',
            token: '',
            shoowPopup: false,
        }

        this.loadProducts = this.loadProducts.bind(this);
        this.messageRegister = this.messageRegister.bind(this);
        this.delete = this.delete.bind(this);
        this.edit = this.edit.bind(this);
        this.setNewValue = this.setNewValue.bind(this);
        this.confirmUpdate = this.confirmUpdate.bind(this);
        this.rejectUpdate = this.rejectUpdate.bind(this);
        this.requestDeleteProduct = this.requestDeleteProduct.bind(this);
    }

    componentDidMount() {
        const { id } = this.props.match.params;
        console.log(id)
        this.setState({token: localStorage.getItem('token')});
        this.loadProducts(id);
    }

    async loadProducts(id) {
        await axiosService({
            method: 'GET', url:'/marketProduct/find',
            params:{stationId: id, page:0, size:0}
        }).then((response) => {
            console.log(response.data.response)
            this.setState({mktProducts: response.data.response});
            this.setState({load: true});
        }).catch((error) => {
            this.setState({load: false});
            this.setState({message: 'falha ao resgatar produtos.',
            typeMessage: 'error'})
        })
    }

    messageRegister() {
        return(
            <div id='stationNotFound'>
                <div id='txtStationNotFound'>Ops... parece que você ainda não
                tem um posto de coleta cadastrado, deseja cadastrar agora?</div>
                <div id='btnsContainerNotFound'>
                    <Link to='/cadastrar' id='btnGenericYes'><div>Sim</div></Link>
                    <Link to='/' id='btnGenericNot'><div>Não</div></Link>
                </div>
            </div>
        )
    }

    requestDeleteProduct(idMktProduct, e) {
        this.setState({shoowPopup:!this.state.shoowPopup});
        if(idMktProduct !== null) {
            this.setState({idMktProduct: idMktProduct});
        }
        if(e !== null) {
            if(e.currentTarget.name === 'sim') {
                console.log(this.state.mktProducts);
                this.delete(this.state.idMktProduct);
            }
        }
    }

    async delete(idMktProduct) {
        console.log(idMktProduct)
        await axiosService({
            method: 'DELETE', url:'/marketProduct/delete',
            params:{idMktProduct: idMktProduct},
            headers:{authorization: this.state.token}
        }).then((response) => {
            console.log(response.data.response)
            this.setState({load: true});
        }).catch((error) => {
            this.setState({load: false});
            this.setState({message: 'falha ao excluir produtos.',
            typeMessage: 'error'})
        })
        let products = [];
        this.state.mktProducts.forEach((p) => {
            if(p.idMktProduct !== this.state.idMktProduct) {
                products.push(p)
            }
        })
        this.setState({mktProducts : products})
    }

    edit(idMktProduct, line){
        debugger;
        this.setState({edit:true})
        this.setState({line:line})
        this.setState({idMktProduct: idMktProduct})
    }
    setNewValue(e) {
        this.setState({newValue:e.target.value})
        e.preventDefault();
    }
    async confirmUpdate() {
        console.log(this.state.idMktProduct);
        console.log(this.state.newValue);
        await axiosService({
            method: 'PUT', url:'/marketProduct/update',
            params:{idMktProduct: this.state.idMktProduct, value: this.state.newValue},
            headers:{authorization: this.state.token}
        }).then((response) => {
            this.setState({load: true});
            this.setState({edit:false});
        }).catch((error) => {
            this.setState({load: false});
            this.setState({message: 'falha ao alterar produto.',
            typeMessage: 'error'})
        })
        let products = this.state.mktProducts;
        products.forEach((p) => {
            if(p.idMktProduct === this.state.idMktProduct) {
                p.value = this.state.newValue;
            }
        })
        this.setState({mktProducts : products, newValue:''})
    }

    rejectUpdate(){
        this.setState({newValue:'', edit:false})
    }



    render(){
        return(
            <div id='containerPage'>
                <div id='titlePage'>Atualizar produtos</div>
                
                {this.state.shoowPopup ? <Popup txt={`Deseja excluir o produto?`} btn1='sim' btn2='não'
                                         close={(e)=>this.requestDeleteProduct(null, e)} />:null}
                
                <div id='containerResultFind'>
                    <div>{this.state.stationNotFound === true && this.messageRegister()}</div>
                    {this.state.load === false ?
                        <MessageLine message={this.state.message} typeMessage={this.state.typeMessage} /> :

                        <table id='tbFind'>
                            <thead>
                                <tr>
                                    <th id='tbFindTh'>Produto</th>
                                    <th id='tbFindTh'>Valor/Kg</th>
                                    <th id='tbFindTh'>Última atualização</th>
                                    <th id='tbFindTh'>Ação</th>
                                </tr>
                            </thead>
                            <tbody>
                            {this.state.mktProducts.map((p, index) => { 
                                return(
                                <tr id='tbFindTrNoHover' style={{'backgroundColor': (index % 2 === 0) ?  '#F3F6F6' : '#FFF'}} key={p.idMktProduct}>
                                    <td id='tbFindTd'>{p.product}</td>
                                    {this.state.edit === true && this.state.line === index ?
                                        <td id='tbFindTd'>         
                                            <div id='inputValueContainer'>
                                                <input id='inputValueTxt' type='text' name='value' value={this.state.newValue}
                                                    placeholder='0,00' onChange={(e) => {this.setNewValue(e)}}/>
                                                <img id='tbIconEditValue' src={require('../../assets/incons/icon-confirm-fill.svg')}
                                                    onClick={()=>{this.confirmUpdate(p.idMktProduct)}} alt='excluir'/>
                                                <img id='tbIconEditValue' src={require('../../assets/incons/icon-delete-fill.svg')}
                                                    onClick={()=>{this.rejectUpdate(p.idMktProduct)}} alt='excluir'/>
                                            </div>
                                        </td>:
                                        <td id='tbFindTd'>{p.value}</td>
                                    }
                                    <td id='tbFindTd'>{p.updateDate}</td>
                                    <td id='tbFindTdBtns'>
                                        <img id='tbIconDuo' src={require('../../assets/incons/icon-delete.svg')}
                                            //  onClick={()=>{this.delete(p.idMktProduct)}} alt='excluir'/>
                                             onClick={()=>this.requestDeleteProduct(p.idMktProduct, null)} alt='excluir'/>
                                        <img id='tbIconDuo' src={require('../../assets/incons/icon-edit.svg')}
                                             onClick={()=>{this.edit(p.idMktProduct, index)}} alt='editar'/>
                                    </td>
                                </tr>
                                ); 
                            },)}
                            </tbody>
                        </table>
                    
                    }
                </div>
            </div>
   
        )
    }
}
export default ProductEdit;
